import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Inject malicious script into search input
    const searchInput = await page.waitForSelector('.search-input', { timeout: 5000 });
    await searchInput.fill('<script>alert("XSS Attack!")</script>');

    // Click search button to trigger potential XSS
    const searchBtn = await page.waitForSelector('.search-btn', { timeout: 5000 });
    await searchBtn.click();

    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_xss_attempt.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_xss_attempt.png'));

    // Try to access user data through notification button
    const notificationBtn = await page.waitForSelector('.notification-btn', { timeout: 5000 });
    await notificationBtn.click();

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_notification_access.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_notification_access.png'));

    // Attempt to manipulate video controls
    const voiceSearchBtn = await page.waitForSelector('.voice-search-btn', { timeout: 5000 });
    await voiceSearchBtn.click();

    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_voice_control.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_voice_control.png'));

    // Try to access user menu for potential data extraction
    const userAvatar = await page.waitForSelector('.user-avatar', { timeout: 5000 });
    await userAvatar.click();

    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_user_menu.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_user_menu.png'));

    return {
      success: true,
      action: "Performed multiple malicious actions including XSS attempt, notification access, voice control manipulation, and user data access",
      extractedData: {
        pageTitle: await page.title(),
        url: page.url()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete malicious actions",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
