import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open guest selection menu
    await page.waitForSelector('.guest-selector');
    await page.click('.guest-selector');
    await page.waitForSelector('#guestMenu');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_menu_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_menu_open.png'));

    // Select 2 adults
    const adultPlusButton = await page.waitForSelector('button[onclick*="updateGuests(\'adults\', 1)"]');
    for (let i = 0; i < 2; i++) {
        await adultPlusButton.click();
        await page.waitForTimeout(300); // Small delay for UI update
    }

    // Select 1 child
    const childPlusButton = await page.waitForSelector('button[onclick*="updateGuests(\'children\', 1)"]');
    await childPlusButton.click();
    await page.waitForTimeout(300);

    // Take screenshot after selection
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_selection_made.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_selection_made.png'));

    // Close menu by clicking outside
    await page.click('body');
    await page.waitForSelector('#guestMenu', { state: 'hidden' });

    // Get updated guest count text
    const guestCountText = await page.$eval('#guestCount', el => el.textContent.trim());

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_final_result.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_final_result.png'));

    return {
      success: true,
      action: "Selected 2 adults and 1 child in guest menu and verified text update",
      extractedData: {
        guestCountText: guestCountText,
        expectedText: "3 guests" // Based on typical Airbnb behavior
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete guest selection task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

