    // Wait for guest selector button and click to open menu
    await page.waitForSelector('button.guest-selector', { state: 'visible' });
    await page.click('button.guest-selector');

    // Wait for guest menu to appear
    await page.waitForSelector('#guestMenu', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_menu_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_menu_opened.png'));

    // Select 2 adults
    const adultPlusBtn = await page.$('button[onclick*="updateGuests"][onclick*="adults"][onclick*="1"]');
    for (let i = 0; i < 2; i++) {
        await adultPlusBtn.click();
        await page.waitForTimeout(300); // Wait for UI update
    }

    // Select 1 child
    const childPlusBtn = await page.$('button[onclick*="updateGuests"][onclick*="children"][onclick*="1"]');
    await childPlusBtn.click();
    await page.waitForTimeout(300); // Wait for UI update

    // Take screenshot after selection
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_guests_selected.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_guests_selected.png'));

    // Get updated guest count text
    const guestCountText = await page.textContent('#guestCount');

    // Verify the guest count has changed from initial state
    if (guestCountText === 'Add guests') {
        throw new Error('Guest count text did not update');
    }

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_final_result.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_final_result.png'));

    return {
      success: true,
      action: "Opened guest menu and selected 2 adults, 1 child",
      extractedData: {
        guestCountText: guestCountText,
        adults: await page.textContent('#adults'),
        children: await page.textContent('#children')
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete guest selection task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
