import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open guest selector menu
    await page.waitForSelector('.guest-selector');
    await page.click('.guest-selector');
    await page.waitForSelector('#guestMenu');

    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_menu_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_menu_open.png'));

    // Select 2 adults
    const adultPlus = await page.$('[onclick*="updateGuests(\'adults\', 1)"]');
    await adultPlus.click();
    await adultPlus.click();

    // Select 1 child
    const childPlus = await page.$('[onclick*="updateGuests(\'children\', 1)"]');
    await childPlus.click();

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_guests_selected.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_guests_selected.png'));

    // Close menu by clicking outside
    await page.click('body');

    // Wait for menu to close and get updated guest count text
    await page.waitForSelector('#guestMenu', { state: 'hidden' });
    const guestText = await page.textContent('#guestCount');

    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_final.png'));

    return {
      success: true,
      action: "Selected 2 adults and 1 child in guest menu",
      extractedData: {
        guestCountText: guestText
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete guest selection task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click guest selector button
    await page.waitForSelector('button.guest-selector', { timeout: 5000 });
    await page.click('button.guest-selector');

    // Wait for guest menu to appear
    await page.waitForSelector('#guestMenu', { state: 'visible', timeout: 5000 });

    // Take screenshot after opening guest menu
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_menu_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_menu_open.png'));

    // Increase adults count by 2
    const adultPlusButton = await page.waitForSelector('button[onclick*="updateGuests(\'adults\', 1)"]');
    await adultPlusButton.click();
    await adultPlusButton.click();

    // Increase children count by 1
    const childPlusButton = await page.waitForSelector('button[onclick*="updateGuests(\'children\', 1)"]');
    await childPlusButton.click();

    // Wait for counts to update
    await page.waitForTimeout(1000);

    // Take screenshot after selecting guests
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_guests_selected.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_guests_selected.png'));

    // Get updated guest count text
    const guestCountText = await page.$eval('#guestCount', el => el.textContent.trim());

    // Extract individual counts
    const adultCount = await page.$eval('#adults', el => parseInt(el.textContent));
    const childCount = await page.$eval('#children', el => parseInt(el.textContent));

    return {
      success: true,
      action: "Opened guest menu and selected 2 adults and 1 child",
      extractedData: {
        guestCountText,
        adultCount,
        childCount,
        totalGuests: adultCount + childCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete guest selection task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open guest selection menu using multiple selector strategies
    const guestSelector = await page.waitForSelector('button.guest-selector, [onclick*="toggleGuestMenu"]', { state: 'visible' });
    await guestSelector.click();

    // Wait for guest menu to appear
    await page.waitForSelector('#guestMenu', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_menu_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_menu_opened.png'));

    // Select 2 adults
    const adultPlus = await page.waitForSelector('button[onclick*="updateGuests(\'adults\', 1)"]');
    await adultPlus.click();
    await adultPlus.click();

    // Select 1 child
    const childPlus = await page.waitForSelector('button[onclick*="updateGuests(\'children\', 1)"]');
    await childPlus.click();

    // Wait for counts to update and verify
    await page.waitForFunction(() => {
      const adults = document.getElementById('adults');
      const children = document.getElementById('children');
      return adults && children && adults.textContent === '2' && children.textContent === '1';
    });

    // Verify guest count text
    const guestCountText = await page.$eval('#guestCount', el => el.textContent);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_guests_selected.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_guests_selected.png'));

    return {
      success: true,
      action: "Opened guest menu and selected 2 adults and 1 child",
      extractedData: {
        adults: 2,
        children: 1,
        guestCountText: guestCountText.trim()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete guest selection task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
