import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open the guest selection menu
    await page.waitForSelector('.guest-selector');
    await page.click('.guest-selector');
    await page.waitForSelector('#guestMenu');

    // Select 2 adults
    await page.waitForSelector('button:contains("+")'); // Wait for the '+' button for adults
    for (let i = 0; i < 2; i++) {
      await page.click('button:contains("+")'); // Click to add an adult
    }

    // Select 1 child
    await page.waitForSelector('button:contains("+")'); // Wait for the '+' button for children
    await page.click('button:contains("+")'); // Click to add a child

    // Take screenshot after selections
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_guest_selection.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_guest_selection.png'));

    // Verify the guest count display text
    const guestCountText = await page.$eval('#guestCount', el => el.textContent);
    const expectedText = '2 adults, 1 child'; // Expected text after selection
    if (guestCountText !== expectedText) {
      throw new Error(`Guest count text does not match. Expected: "${expectedText}", but got: "${guestCountText}"`);
    }

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_final_verification.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_final_verification.png'));

    return {
      success: true,
      action: "Successfully opened guest selection menu and selected 2 adults and 1 child.",
      extractedData: { guestCountText },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

