import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Open the guest selection menu
    await page.waitForSelector('.guest-selector');
    await page.click('.guest-selector');

    // Wait for the guest menu to appear
    await page.waitForSelector('#guestMenu');

    // Increase the number of adults to 2
    await page.waitForSelector('button:contains("+")'); // Wait for the "+" button for adults
    for (let i = 0; i < 2; i++) {
      await page.click('button:contains("+")'); // Click the "+" button for adults
      await page.waitForTimeout(100); // Small wait to ensure the UI updates
    }

    // Increase the number of children to 1
    await page.waitForSelector('button:contains("+")'); // Wait for the "+" button for children
    await page.click('button:contains("+")'); // Click the "+" button for children

    // Take a screenshot after updating the guest count
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_guest_count_updated.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_guest_count_updated.png'));

    // Extract the updated guest count text
    const guestCountText = await page.$eval('#guestCount', el => el.textContent);

    return {
      success: true,
      action: "Successfully updated guest count to 2 adults and 1 child",
      extractedData: { guestCount: guestCountText },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
