    // Verify the guest count text
    await page.waitForSelector('#guestCount');
    const guestCountText = await page.$eval('#guestCount', el => el.textContent);

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_final.png'));

    return {
      success: true,
      action: "Selected 2 adults and 1 child, verified guest count text",
      extractedData: { guestCount: guestCountText },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

