import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Set check-in date to tomorrow
    const tomorrow = new Date();
    tomorrow.setDate(tomorrow.getDate() + 1);
    const checkinDate = tomorrow.toISOString().split('T')[0];

    // Set check-out date to the day after tomorrow
    const dayAfterTomorrow = new Date();
    dayAfterTomorrow.setDate(dayAfterTomorrow.getDate() + 2);
    const checkoutDate = dayAfterTomorrow.toISOString().split('T')[0];

    // Input check-in date
    await page.waitForSelector('#checkinInput');
    await page.fill('#checkinInput', checkinDate);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_checkin_date_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_checkin_date_set.png'));

    // Input check-out date
    await page.waitForSelector('#checkoutInput');
    await page.fill('#checkoutInput', checkoutDate);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_checkout_date_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_checkout_date_set.png'));

    // Verify the input values
    const checkinValue = await page.inputValue('#checkinInput');
    const checkoutValue = await page.inputValue('#checkoutInput');

    const isCheckinCorrect = checkinValue === checkinDate;
    const isCheckoutCorrect = checkoutValue === checkoutDate;

    return {
      success: true,
      action: "Set check-in and check-out dates and verified the values",
      extractedData: {
        checkinValue,
        checkoutValue,
        isCheckinCorrect,
        isCheckoutCorrect
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

