import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Get tomorrow's date and the day after tomorrow's date
    const today = new Date();
    const checkinDate = new Date(today);
    checkinDate.setDate(today.getDate() + 1); // Tomorrow
    const checkoutDate = new Date(today);
    checkoutDate.setDate(today.getDate() + 2); // Day after tomorrow

    // Format the dates to YYYY-MM-DD
    const formattedCheckin = checkinDate.toISOString().split('T')[0];
    const formattedCheckout = checkoutDate.toISOString().split('T')[0];

    // Select the check-in and check-out input fields and set their values
    await page.waitForSelector('#checkinInput');
    await page.fill('#checkinInput', formattedCheckin);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_checkin.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_checkin.png'));

    await page.waitForSelector('#checkoutInput');
    await page.fill('#checkoutInput', formattedCheckout);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_checkout.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_checkout.png'));

    // Verify that the values were set correctly
    const checkinValue = await page.inputValue('#checkinInput');
    const checkoutValue = await page.inputValue('#checkoutInput');

    // Check if the input values are correct
    const isCheckinCorrect = checkinValue === formattedCheckin;
    const isCheckoutCorrect = checkoutValue === formattedCheckout;

    return {
      success: isCheckinCorrect && isCheckoutCorrect,
      action: "Set check-in and check-out dates correctly",
      extractedData: {
        checkinValue,
        checkoutValue,
        expectedCheckin: formattedCheckin,
        expectedCheckout: formattedCheckout
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
