import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Set check-in date to tomorrow
    const tomorrow = new Date();
    tomorrow.setDate(tomorrow.getDate() + 1);
    const checkinDate = tomorrow.toISOString().split('T')[0];

    // Set check-out date to the day after tomorrow
    const dayAfterTomorrow = new Date();
    dayAfterTomorrow.setDate(dayAfterTomorrow.getDate() + 2);
    const checkoutDate = dayAfterTomorrow.toISOString().split('T')[0];

    // Wait for check-in input and set the date
    await page.waitForSelector('#checkinInput');
    await page.fill('#checkinInput', checkinDate);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_checkin.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_checkin.png'));

    // Wait for check-out input and set the date
    await page.waitForSelector('#checkoutInput');
    await page.fill('#checkoutInput', checkoutDate);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_checkout.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_checkout.png'));

    // Verify the values in the date input fields
    const checkinValue = await page.inputValue('#checkinInput');
    const checkoutValue = await page.inputValue('#checkoutInput');

    // Check if the values are set correctly
    const isCheckinCorrect = checkinValue === checkinDate;
    const isCheckoutCorrect = checkoutValue === checkoutDate;

    return {
      success: isCheckinCorrect && isCheckoutCorrect,
      action: "Set check-in and check-out dates",
      extractedData: {
        checkin: checkinValue,
        checkout: checkoutValue
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

