import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Set check-in date to tomorrow
    const checkinInputSelector = '#checkinInput';
    const checkoutInputSelector = '#checkoutInput';

    // Get today's date and calculate tomorrow and the day after tomorrow
    const today = new Date();
    const tomorrow = new Date(today);
    tomorrow.setDate(today.getDate() + 1);
    const dayAfterTomorrow = new Date(today);
    dayAfterTomorrow.setDate(today.getDate() + 2);

    // Format dates to YYYY-MM-DD
    const formattedCheckin = tomorrow.toISOString().split('T')[0];
    const formattedCheckout = dayAfterTomorrow.toISOString().split('T')[0];

    // Wait for check-in input and set value
    await page.waitForSelector(checkinInputSelector);
    await page.fill(checkinInputSelector, formattedCheckin);

    // Wait for check-out input and set value
    await page.waitForSelector(checkoutInputSelector);
    await page.fill(checkoutInputSelector, formattedCheckout);

    // Take screenshot after setting dates
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_dates_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_dates_set.png'));

    // Verify that the dates are set correctly
    const checkinValue = await page.inputValue(checkinInputSelector);
    const checkoutValue = await page.inputValue(checkoutInputSelector);

    // Check if the values are correct
    const isCheckinCorrect = checkinValue === formattedCheckin;
    const isCheckoutCorrect = checkoutValue === formattedCheckout;

    return {
      success: isCheckinCorrect && isCheckoutCorrect,
      action: "Set check-in date to tomorrow and check-out date to the day after tomorrow",
      extractedData: {
        checkin: checkinValue,
        checkout: checkoutValue,
        isCheckinCorrect,
        isCheckoutCorrect
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

