import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Calculate tomorrow and day after tomorrow dates
    const today = new Date();
    const tomorrow = new Date(today);
    tomorrow.setDate(today.getDate() + 1);

    const dayAfterTomorrow = new Date(today);
    dayAfterTomorrow.setDate(today.getDate() + 2);

    // Format dates to YYYY-MM-DD (HTML date input format)
    const formatDate = (date) => date.toISOString().split('T')[0];
    const tomorrowFormatted = formatDate(tomorrow);
    const dayAfterTomorrowFormatted = formatDate(dayAfterTomorrow);

    // Wait for date inputs and set values
    await page.waitForSelector('#checkinInput');
    await page.waitForSelector('#checkoutInput');

    await page.fill('#checkinInput', tomorrowFormatted);
    await page.fill('#checkoutInput', dayAfterTomorrowFormatted);

    // Take screenshot after setting dates
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_dates_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_dates_set.png'));

    // Verify the dates are set correctly
    const checkinValue = await page.$eval('#checkinInput', el => el.value);
    const checkoutValue = await page.$eval('#checkoutInput', el => el.value);

    const datesCorrect = checkinValue === tomorrowFormatted && checkoutValue === dayAfterTomorrowFormatted;

    // Take verification screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_verification.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_verification.png'));

    return {
      success: datesCorrect,
      action: "Set check-in to tomorrow and check-out to day after tomorrow, then verified dates",
      extractedData: {
        checkin: checkinValue,
        checkout: checkoutValue,
        expectedCheckin: tomorrowFormatted,
        expectedCheckout: dayAfterTomorrowFormatted,
        datesMatch: datesCorrect
      },
      screenshots,
      error: datesCorrect ? null : 'Dates were not set correctly'
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

