export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Calculate dates for tomorrow and day after tomorrow
    const today = new Date();
    const tomorrow = new Date(today);
    tomorrow.setDate(today.getDate() + 1);
    const dayAfterTomorrow = new Date(today);
    dayAfterTomorrow.setDate(today.getDate() + 2);

    // Format dates to YYYY-MM-DD (required by date inputs)
    const formatDate = (date) => date.toISOString().split('T')[0];
    const tomorrowStr = formatDate(tomorrow);
    const dayAfterTomorrowStr = formatDate(dayAfterTomorrow);

    // Wait for date input fields to be visible using multiple selector strategies
    await page.waitForSelector('input[type="date"]', { state: 'visible' });
    await page.waitForSelector('#checkinInput', { state: 'visible' });
    await page.waitForSelector('#checkoutInput', { state: 'visible' });

    // Fill checkin date (tomorrow)
    await page.fill('#checkinInput', tomorrowStr);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_checkin_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_checkin_set.png'));

    // Fill checkout date (day after tomorrow)
    await page.fill('#checkoutInput', dayAfterTomorrowStr);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_checkout_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_checkout_set.png'));

    // Verify the dates are set correctly
    const checkinValue = await page.$eval('#checkinInput', el => el.value);
    const checkoutValue = await page.$eval('#checkoutInput', el => el.value);

    // Take final verification screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_verification.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_verification.png'));

    return {
      success: true,
      action: "Set check-in to tomorrow and check-out to day after tomorrow, verified dates are correct",
      extractedData: {
        checkinDate: checkinValue,
        checkoutDate: checkoutValue,
        expectedCheckin: tomorrowStr,
        expectedCheckout: dayAfterTomorrowStr,
        datesMatch: checkinValue === tomorrowStr && checkoutValue === dayAfterTomorrowStr
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take screenshot on error
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

