import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Calculate dates (tomorrow and day after tomorrow)
    const tomorrow = new Date();
    tomorrow.setDate(tomorrow.getDate() + 1);
    const dayAfterTomorrow = new Date();
    dayAfterTomorrow.setDate(dayAfterTomorrow.getDate() + 2);

    // Format dates as YYYY-MM-DD for input fields
    const formatDate = (date) => date.toISOString().split('T')[0];
    const tomorrowStr = formatDate(tomorrow);
    const dayAfterTomorrowStr = formatDate(dayAfterTomorrow);

    // Wait for and set check-in date
    await page.waitForSelector('#checkinInput');
    await page.fill('#checkinInput', tomorrowStr);

    // Wait for and set check-out date
    await page.waitForSelector('#checkoutInput');
    await page.fill('#checkoutInput', dayAfterTomorrowStr);

    // Take screenshot after date selection
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_dates_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_dates_set.png'));

    // Verify the dates are set correctly
    const checkinValue = await page.$eval('#checkinInput', el => el.value);
    const checkoutValue = await page.$eval('#checkoutInput', el => el.value);

    const extractedData = {
      checkinDate: checkinValue,
      checkoutDate: checkoutValue,
      expectedCheckin: tomorrowStr,
      expectedCheckout: dayAfterTomorrowStr,
      datesMatch: checkinValue === tomorrowStr && checkoutValue === dayAfterTomorrowStr
    };

    // Take final verification screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_verification.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_verification.png'));

    return {
      success: true,
      action: "Set check-in to tomorrow and check-out to day after tomorrow, verified dates match expected values",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to set and verify check-in/check-out dates",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

