import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Calculate dates for tomorrow and day after tomorrow
    const today = new Date();
    const tomorrow = new Date(today);
    tomorrow.setDate(tomorrow.getDate() + 1);
    const dayAfterTomorrow = new Date(tomorrow);
    dayAfterTomorrow.setDate(dayAfterTomorrow.getDate() + 1);

    // Format dates to YYYY-MM-DD for input fields
    const formatDate = (date) => date.toISOString().split('T')[0];
    const checkinDate = formatDate(tomorrow);
    const checkoutDate = formatDate(dayAfterTomorrow);

    // Wait for and fill checkin date
    await page.waitForSelector('#checkinInput', { state: 'visible' });
    await page.fill('#checkinInput', checkinDate);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_checkin_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_checkin_set.png'));

    // Wait for and fill checkout date
    await page.waitForSelector('#checkoutInput', { state: 'visible' });
    await page.fill('#checkoutInput', checkoutDate);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_checkout_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_checkout_set.png'));

    // Verify the dates were set correctly
    const actualCheckin = await page.$eval('#checkinInput', el => el.value);
    const actualCheckout = await page.$eval('#checkoutInput', el => el.value);

    if (actualCheckin !== checkinDate || actualCheckout !== checkoutDate) {
      throw new Error(`Date verification failed. Expected: ${checkinDate}/${checkoutDate}, Got: ${actualCheckin}/${actualCheckout}`);
    }

    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_verification.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_verification.png'));

    return {
      success: true,
      action: "Set check-in to tomorrow and check-out to day after tomorrow, verified dates match expected values",
      extractedData: {
        checkinDate: actualCheckin,
        checkoutDate: actualCheckout,
        expectedCheckin: checkinDate,
        expectedCheckout: checkoutDate
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete date setting and verification task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
