import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Calculate tomorrow and day after tomorrow dates
    const tomorrow = new Date();
    tomorrow.setDate(tomorrow.getDate() + 1);
    const dayAfterTomorrow = new Date();
    dayAfterTomorrow.setDate(dayAfterTomorrow.getDate() + 2);

    // Format dates as YYYY-MM-DD for date inputs
    const formatDate = (date) => date.toISOString().split('T')[0];
    const tomorrowStr = formatDate(tomorrow);
    const dayAfterTomorrowStr = formatDate(dayAfterTomorrow);

    // Wait for date input fields to be visible
    await page.waitForSelector('#checkinInput', { state: 'visible' });
    await page.waitForSelector('#checkoutInput', { state: 'visible' });

    // Set checkin date to tomorrow
    await page.fill('#checkinInput', tomorrowStr);

    // Set checkout date to day after tomorrow
    await page.fill('#checkoutInput', dayAfterTomorrowStr);

    // Take screenshot after setting dates
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_dates_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_dates_set.png'));

    // Verify the dates were set correctly
    const checkinValue = await page.$eval('#checkinInput', el => el.value);
    const checkoutValue = await page.$eval('#checkoutInput', el => el.value);

    // Take verification screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_verification.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_verification.png'));

    return {
      success: true,
      action: "Set checkin to tomorrow and checkout to day after tomorrow, verified dates are correct",
      extractedData: {
        checkinDate: checkinValue,
        checkoutDate: checkoutValue,
        expectedCheckin: tomorrowStr,
        expectedCheckout: dayAfterTomorrowStr,
        datesMatch: checkinValue === tomorrowStr && checkoutValue === dayAfterTomorrowStr
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

