import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Calculate tomorrow and day after tomorrow dates
    const today = new Date();
    const tomorrow = new Date(today);
    tomorrow.setDate(today.getDate() + 1);

    const dayAfterTomorrow = new Date(today);
    dayAfterTomorrow.setDate(today.getDate() + 2);

    // Format dates as YYYY-MM-DD for input fields
    const formatDate = (date) => {
      const year = date.getFullYear();
      const month = String(date.getMonth() + 1).padStart(2, '0');
      const day = String(date.getDate()).padStart(2, '0');
      return `${year}-${month}-${day}`;
    };

    const checkinDate = formatDate(tomorrow);
    const checkoutDate = formatDate(dayAfterTomorrow);

    // Wait for and set check-in date
    await page.waitForSelector('#checkinInput');
    await page.fill('#checkinInput', checkinDate);

    // Wait for and set check-out date
    await page.waitForSelector('#checkoutInput');
    await page.fill('#checkoutInput', checkoutDate);

    // Take screenshot after setting dates
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_dates_set.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_dates_set.png'));

    // Verify the dates are set correctly
    const actualCheckin = await page.$eval('#checkinInput', el => el.value);
    const actualCheckout = await page.$eval('#checkoutInput', el => el.value);

    // Take verification screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_verification.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_verification.png'));

    return {
      success: true,
      action: "Set check-in to tomorrow and check-out to day after tomorrow",
      extractedData: {
        expectedCheckin: checkinDate,
        actualCheckin,
        expectedCheckout: checkoutDate,
        actualCheckout,
        datesMatch: actualCheckin === checkinDate && actualCheckout === checkoutDate
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to set and verify check-in/check-out dates",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

