import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click the 'Next' button 3 times
    for (let i = 0; i < 3; i++) {
      await page.waitForSelector('.listing-gallery .gallery-nav.next');
      await page.click('.listing-gallery .gallery-nav.next');
      await page.waitForTimeout(500); // Wait for the image to load
    }

    // Verify the current image index
    const currentIndex = await page.evaluate(() => {
      const images = document.querySelectorAll('.listing-gallery-images img');
      return Array.from(images).findIndex(img => img.classList.contains('active'));
    });

    // Take screenshot after clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_clicks.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_clicks.png'));

    return {
      success: true,
      action: "Clicked 'Next' button 3 times and retrieved current image index",
      extractedData: { currentIndex },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

