import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the first accommodation card to load
    await page.waitForSelector('.listing-gallery');

    // Click the 'Next' button three times
    const nextButtonSelector = '.listing-gallery .gallery-nav.next';
    for (let i = 0; i < 3; i++) {
      await page.waitForSelector(nextButtonSelector);
      await page.click(nextButtonSelector);
      await page.waitForTimeout(500); // Wait for the transition to complete
    }

    // Extract the current image index from the gallery
    const currentIndex = await page.evaluate(() => {
      const images = document.querySelectorAll('.listing-gallery-images img');
      return images.length > 0 ? Array.from(images).indexOf(images[images.length - 1]) + 1 : 0; // +1 to convert index to a 1-based count
    });

    // Take a final screenshot to confirm the current index
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_final.png'));

    return {
      success: true,
      action: "Clicked 'Next' button 3 times and retrieved the current image index",
      extractedData: { currentIndex },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
