import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on Jun's first listing favorite button
    await page.waitForSelector("[data-listing-id='jun_1'] .listing-favorite");
    await page.click("[data-listing-id='jun_1'] .listing-favorite");

    // Wait for the button state to change (e.g., check if it becomes active)
    await page.waitForSelector("[data-listing-id='jun_1'] .listing-favorite.active");

    // Check localStorage for updates
    const localStorageData = await page.evaluate(() => JSON.stringify(localStorage));
    const favoritesUpdated = JSON.parse(localStorageData).favorites.includes('jun_1');

    // Take screenshot after clicking the favorite button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_favorite_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_favorite_clicked.png'));

    return {
      success: true,
      action: "Clicked Jun's first listing favorite button and verified localStorage update",
      extractedData: { favoritesUpdated },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

