import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the favorite button for Jun's listing to be visible
    await page.waitForSelector("[data-listing-id='jun_1'] .listing-favorite");

    // Click the favorite button
    await page.click("[data-listing-id='jun_1'] .listing-favorite");

    // Wait for the button state change (could be a class change or inner text change)
    await page.waitForFunction(() => {
      const button = document.querySelector("[data-listing-id='jun_1'] .listing-favorite");
      return button.classList.contains('active') || button.innerText.includes('Saved');
    });

    // Check the localStorage for updates
    const localStorageData = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('favorites'));
    });

    // Take a screenshot after clicking the favorite button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_click.png'));

    // Return the status and extracted data
    return {
      success: true,
      action: "Clicked the favorite button for Jun's first listing and verified state change.",
      extractedData: { favorites: localStorageData },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
