import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on Jun's first listing favorite button
    const favoriteButtonSelector = "[data-listing-id='jun_1'] .listing-favorite";
    await page.waitForSelector(favoriteButtonSelector);
    await page.click(favoriteButtonSelector);

    // Wait for the button state to change (e.g., from not favorite to favorite)
    await page.waitForFunction(
      (selector) => {
        const button = document.querySelector(selector);
        return button && button.classList.contains('favorited'); // Assuming 'favorited' is the class when it's a favorite
      },
      {},
      favoriteButtonSelector
    );

    // Take screenshot after clicking the button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_click.png'));

    // Check localStorage for favorite updates
    const localStorageFavorites = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('favorites')) || [];
    });

    // Verify that 'jun_1' is in the favorites list
    const isFavoriteUpdated = localStorageFavorites.includes('jun_1');

    return {
      success: true,
      action: "Clicked the favorite button for Jun's first listing and verified localStorage update.",
      extractedData: {
        isFavoriteUpdated,
        localStorageFavorites
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
