import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the favorite button for Jun's first listing
    const favoriteButtonSelector = "[data-listing-id='jun_1'] .listing-favorite";
    await page.waitForSelector(favoriteButtonSelector);
    await page.click(favoriteButtonSelector);

    // Take screenshot after clicking the favorite button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_favorite_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_favorite_clicked.png'));

    // Verify the button state change (assuming it toggles a class or attribute)
    const isFavorited = await page.evaluate(selector => {
      const button = document.querySelector(selector);
      return button.classList.contains('favorited'); // Adjust based on actual implementation
    }, favoriteButtonSelector);

    // Check localStorage for updates
    const favorites = await page.evaluate(() => localStorage.getItem('favorites'));
    const favoritesList = JSON.parse(favorites);

    // Take screenshot of the localStorage state
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_local_storage.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_local_storage.png'));

    return {
      success: true,
      action: "Clicked on Jun's first listing favorite button and verified state change and localStorage update",
      extractedData: {
        isFavorited,
        favoritesList
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

