import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the favorite button for Jun's first listing to be visible
    await page.waitForSelector("[data-listing-id='jun_1'] .listing-favorite");

    // Click the favorite button
    await page.click("[data-listing-id='jun_1'] .listing-favorite");

    // Take a screenshot after clicking the favorite button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_favorite_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_favorite_clicked.png'));

    // Verify the button state has changed (e.g., it may have a different class or text)
    const isFavorited = await page.evaluate(() => {
      const button = document.querySelector("[data-listing-id='jun_1'] .listing-favorite");
      return button.classList.contains('favorited'); // Adjust class name based on actual implementation
    });

    // Check localStorage for updates
    const favorites = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('favorites'));
    });

    // Take a screenshot of the localStorage state
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_local_storage.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_local_storage.png'));

    // Return the results
    return {
      success: true,
      action: "Clicked the favorite button for Jun's first listing and verified state change and localStorage update.",
      extractedData: {
        isFavorited,
        favorites
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

