import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the favorite button of Jun's first listing to be visible
    await page.waitForSelector("[data-listing-id='jun_1'] .listing-favorite", { timeout: 5000 });

    // Click on the favorite button
    await page.click("[data-listing-id='jun_1'] .listing-favorite");
    await page.waitForTimeout(1000); // Wait for the button state to change

    // Check the state of the favorite button
    const isFavorited = await page.evaluate(() => {
      const button = document.querySelector("[data-listing-id='jun_1'] .listing-favorite");
      return button.classList.contains('active'); // Assuming 'active' indicates it's favorited
    });

    // Check localStorage for the updated favorites
    const favoritesList = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('favorites')) || [];
    });

    // Take a screenshot after clicking the favorite button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_favorited.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_favorited.png'));

    return {
      success: true,
      action: "Clicked on Jun's first listing favorite button and checked state change",
      extractedData: {
        isFavorited,
        favoritesList
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
