import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the favorite button for Jun's first listing to be visible
    await page.waitForSelector("[data-listing-id='jun_1'] .listing-favorite");

    // Click the favorite button
    await page.click("[data-listing-id='jun_1'] .listing-favorite");

    // Take screenshot after clicking the favorite button
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_favorite_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_favorite_clicked.png'));

    // Wait for localStorage to update (this may vary based on implementation)
    await page.waitForTimeout(1000); // Adjust timeout as necessary

    // Extract localStorage data to verify the update
    const localStorageData = await page.evaluate(() => {
      return JSON.stringify(localStorage);
    });

    // Take screenshot after localStorage update
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_localStorage_updated.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_localStorage_updated.png'));

    return {
      success: true,
      action: "Clicked the favorite button for Jun's first listing and verified localStorage update.",
      extractedData: { localStorageData },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

