import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and locate Jun's first listing
    await page.waitForSelector("[data-listing-id='jun_1']", { timeout: 10000 });
    const junListing = await page.$("[data-listing-id='jun_1']");

    // Get initial localStorage state
    const initialFavorites = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('airbnb_favorites') || '[]');
    });

    // Take screenshot before interaction
    await junListing.screenshot({ path: path.join(screenshotsDir, 'step_2_before_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_click.png'));

    // Click the favorite button
    const favoriteButton = await junListing.$('.listing-favorite');
    await favoriteButton.click();

    // Wait for animation/state change
    await page.waitForTimeout(1000);

    // Take screenshot after interaction
    await junListing.screenshot({ path: path.join(screenshotsDir, 'step_3_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_click.png'));

    // Get updated localStorage state
    const updatedFavorites = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('airbnb_favorites') || '[]');
    });

    // Check button state change
    const buttonClass = await favoriteButton.evaluate(el => el.className);
    const isFavorited = buttonClass.includes('favorited') || buttonClass.includes('active');

    return {
      success: true,
      action: "Clicked favorite button for Jun's first listing and verified state change",
      extractedData: {
        initialFavorites,
        updatedFavorites,
        isFavorited,
        junListingId: 'jun_1'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

