import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for listings to load
    await page.waitForSelector('[data-listing-id]', { timeout: 5000 });

    // Find Jun's first listing using XPath
    const junListing = await page.$x("//*[@data-listing-id='jun_1']");
    if (junListing.length === 0) {
      throw new Error('Jun listing not found');
    }

    // Get favorite button using multiple selector strategies
    const favoriteButton = await page.$("[data-listing-id='jun_1'] .listing-favorite");
    if (!favoriteButton) {
      throw new Error('Favorite button not found');
    }

    // Take screenshot before clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_click.png'));

    // Get initial localStorage state
    const initialStorage = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('airbnb_favorites') || '[]');
    });

    // Click the favorite button
    await favoriteButton.click();

    // Wait for animation/state change
    await page.waitForTimeout(1000);

    // Take screenshot after clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_click.png'));

    // Get updated localStorage state
    const updatedStorage = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('airbnb_favorites') || '[]');
    });

    // Check if button state changed by looking for active class
    const buttonState = await page.evaluate((selector) => {
      const btn = document.querySelector(selector);
      return {
        hasActiveClass: btn.classList.contains('active'),
        hasFavoritedClass: btn.classList.contains('favorited'),
        ariaPressed: btn.getAttribute('aria-pressed'),
        innerHTML: btn.innerHTML
      };
    }, "[data-listing-id='jun_1'] .listing-favorite");

    return {
      success: true,
      action: "Clicked favorite button on Jun's first listing and verified state change",
      extractedData: {
        initialFavorites: initialStorage,
        updatedFavorites: updatedStorage,
        buttonState: buttonState,
        junListingId: 'jun_1'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
