import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and locate Jun's first listing (jun_1)
    const junListingSelector = "[data-listing-id='jun_1']";
    await page.waitForSelector(junListingSelector);

    // Wait for favorite button within the listing
    const favoriteButtonSelector = `${junListingSelector} .listing-favorite`;
    await page.waitForSelector(favoriteButtonSelector);

    // Get initial button state and localStorage
    const initialButtonClass = await page.$eval(favoriteButtonSelector, el => el.className);
    const initialLocalStorage = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('favorites') || '[]');
    });

    // Take screenshot before click
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_click.png'));

    // Click the favorite button
    await page.click(favoriteButtonSelector);

    // Wait for UI update (500ms should be sufficient for class changes)
    await page.waitForTimeout(500);

    // Get updated button state
    const updatedButtonClass = await page.$eval(favoriteButtonSelector, el => el.className);

    // Get updated localStorage
    const updatedLocalStorage = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('favorites') || '[]');
    });

    // Take screenshot after click
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_click.png'));

    return {
      success: true,
      action: "Clicked favorite button on Jun's first listing (jun_1) and verified state changes",
      extractedData: {
        initialButtonClass,
        updatedButtonClass,
        initialLocalStorage,
        updatedLocalStorage,
        jun_1_in_favorites: updatedLocalStorage.includes('jun_1')
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

