import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for listings to load
    await page.waitForSelector('[data-listing-id]', { timeout: 10000 });

    // Find Jun's first listing
    const junListing = await page.$('[data-listing-id="jun_1"]');
    if (!junListing) {
      throw new Error('Jun listing not found');
    }

    // Take screenshot before interaction
    await junListing.screenshot({ path: path.join(screenshotsDir, 'step_2_before_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_click.png'));

    // Get initial localStorage state
    const initialFavorites = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('favorites') || '[]');
    });

    // Get initial button state
    const initialButtonClass = await page.evaluate(() => {
      const btn = document.querySelector('[data-listing-id="jun_1"] .listing-favorite');
      return btn?.className || '';
    });

    // Click the favorite button
    await page.click('[data-listing-id="jun_1"] .listing-favorite');

    // Wait for state changes
    await page.waitForTimeout(1000);

    // Get updated localStorage state
    const updatedFavorites = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('favorites') || '[]');
    });

    // Get updated button state
    const updatedButtonClass = await page.evaluate(() => {
      const btn = document.querySelector('[data-listing-id="jun_1"] .listing-favorite');
      return btn?.className || '';
    });

    // Take screenshot after interaction
    await junListing.screenshot({ path: path.join(screenshotsDir, 'step_3_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_click.png'));

    return {
      success: true,
      action: "Clicked Jun's first listing favorite button and verified state changes",
      extractedData: {
        initialFavorites,
        updatedFavorites,
        initialButtonClass,
        updatedButtonClass,
        wasAdded: !initialFavorites.includes('jun_1') && updatedFavorites.includes('jun_1'),
        wasRemoved: initialFavorites.includes('jun_1') && !updatedFavorites.includes('jun_1')
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
