import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the first listing with data-listing-id='jun_1'
    const listingSelector = "[data-listing-id='jun_1']";
    await page.waitForSelector(listingSelector, { timeout: 5000 });

    // Find the favorite button within the listing
    const favoriteButton = await page.$(`${listingSelector} .listing-favorite`);

    if (!favoriteButton) {
      throw new Error('Favorite button not found in Jun\'s first listing');
    }

    // Take screenshot before clicking
    await favoriteButton.screenshot({ path: path.join(screenshotsDir, 'step_2_before_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_click.png'));

    // Get initial localStorage state
    const initialFavorites = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('favorites') || '[]');
    });

    // Click the favorite button
    await favoriteButton.click();

    // Wait for state change
    await page.waitForTimeout(1000);

    // Take screenshot after clicking
    await favoriteButton.screenshot({ path: path.join(screenshotsDir, 'step_3_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_click.png'));

    // Get updated localStorage state
    const updatedFavorites = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('favorites') || '[]');
    });

    // Check if button class changed (indicating favorite state)
    const buttonClass = await favoriteButton.evaluate(el => el.className);
    const isFavorited = buttonClass.includes('favorited') || buttonClass.includes('active');

    return {
      success: true,
      action: "Clicked favorite button on Jun's first listing and verified state change",
      extractedData: {
        initialFavorites,
        updatedFavorites,
        isFavorited,
        buttonClass
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
