import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for listings to load
    await page.waitForSelector('[data-listing-id]', { timeout: 10000 });

    // Get initial localStorage state
    const initialStorage = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('favorites') || '[]');
    });

    // Find and interact with Jun's first listing favorite button
    const junListing = await page.waitForSelector('[data-listing-id="jun_1"]');

    // Get initial button state
    const favoriteButton = await junListing.$('.listing-favorite');
    const initialButtonClass = await favoriteButton.getAttribute('class');

    // Take screenshot before click
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_click.png'));

    // Click the favorite button
    await favoriteButton.click();
    await page.waitForTimeout(1000); // Wait for state changes

    // Get updated button state
    const updatedButtonClass = await favoriteButton.getAttribute('class');

    // Get updated localStorage state
    const updatedStorage = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('favorites') || '[]');
    });

    // Take screenshot after click
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_click.png'));

    return {
      success: true,
      action: "Clicked favorite button for Jun's first listing and verified state changes",
      extractedData: {
        initialButtonState: initialButtonClass,
        updatedButtonState: updatedButtonClass,
        initialStorage: initialStorage,
        updatedStorage: updatedStorage,
        jun1InInitialStorage: initialStorage.includes('jun_1'),
        jun1InUpdatedStorage: updatedStorage.includes('jun_1')
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

