    const favoriteBtn = junListing.locator('.listing-favorite');
    await favoriteBtn.waitFor({ timeout: 5000 });

    // Take pre-click screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_pre_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_pre_click.png'));

