    // Take post-click screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_post_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_post_click.png'));

    // Verify button state change by checking for active class
    const buttonClass = await favoriteBtn.getAttribute('class');
    const isActive = buttonClass.includes('active') || buttonClass.includes('favorited');

    return {
      success: true,
      action: "Clicked favorite button for Jun's first listing and verified state changes",
      extractedData: {
        initialLocalStorage: initialStorage,
        updatedLocalStorage: updatedStorage,
        buttonClass: buttonClass,
        isFavoriteActive: isActive
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot if possible
    try {
      await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
      screenshots.push(path.join(screenshotsDir, 'error.png'));
    } catch (e) {}

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for listings to load
    await page.waitForSelector('[data-listing-id]', { timeout: 10000 });

    // Find Jun's first listing with data-listing-id='jun_1'
    const junListing = await page.$('[data-listing-id="jun_1"]');
    if (!junListing) {
      throw new Error('Jun listing not found');
    }

    // Wait for favorite button to be visible
    const favoriteBtn = await junListing.$('.listing-favorite');
    if (!favoriteBtn) {
      throw new Error('Favorite button not found');
    }

    // Get initial localStorage state
    const initialStorage = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('airbnb_favorites') || '[]');
    });

    // Take screenshot before click
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_click.png'));

    // Click the favorite button
    await favoriteBtn.click();

    // Wait for animation/state change
    await page.waitForTimeout(1000);

    // Take screenshot after click
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_click.png'));

    // Get updated localStorage state
    const updatedStorage = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('airbnb_favorites') || '[]');
    });

    // Check button state change
    const isFavorited = await page.evaluate((btn) => {
      return btn.classList.contains('favorited') || 
             btn.getAttribute('data-favorited') === 'true';
    }, favoriteBtn);

    return {
      success: true,
      action: "Clicked favorite button on Jun's first listing and verified state change",
      extractedData: {
        initialFavorites: initialStorage,
        updatedFavorites: updatedStorage,
        isNowFavorited: isFavorited,
        listingId: 'jun_1'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
