import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for Jun's first listing to load
    const listingSelector = '[data-listing-id="jun_1"]';
    await page.waitForSelector(listingSelector, { timeout: 10000 });

    // Get initial localStorage state
    const initialLocalStorage = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('airbnb_favorites') || '[]');
    });

    // Get initial favorite button state
    const favoriteButtonSelector = `${listingSelector} .listing-favorite`;
    const initialButtonState = await page.evaluate((selector) => {
      const button = document.querySelector(selector);
      return {
        isFavorite: button?.classList.contains('favorited') || false,
        ariaLabel: button?.getAttribute('aria-label') || ''
      };
    }, favoriteButtonSelector);

    // Take screenshot before click
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_click.png'));

    // Click the favorite button
    await page.click(favoriteButtonSelector);

    // Wait for state change (either favorited or not favorited)
    await page.waitForFunction((selector) => {
      const button = document.querySelector(selector);
      return button && (
        button.classList.contains('favorited') || 
        !button.classList.contains('favorited')
      );
    }, favoriteButtonSelector);

    // Get updated button state
    const updatedButtonState = await page.evaluate((selector) => {
      const button = document.querySelector(selector);
      return {
        isFavorite: button?.classList.contains('favorited') || false,
        ariaLabel: button?.getAttribute('aria-label') || ''
      };
    }, favoriteButtonSelector);

    // Get updated localStorage state
    const updatedLocalStorage = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('airbnb_favorites') || '[]');
    });

    // Take screenshot after state change
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_click.png'));

    return {
      success: true,
      action: "Clicked favorite button on Jun's first listing and verified state change",
      extractedData: {
        initialButtonState,
        updatedButtonState,
        initialLocalStorage,
        updatedLocalStorage,
        favoritesChanged: initialLocalStorage.length !== updatedLocalStorage.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

