import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for listings to load
    await page.waitForSelector('[data-listing-id]', { timeout: 10000 });

    // Find Jun's first listing
    const junListing = await page.$('[data-listing-id="jun_1"]');
    if (!junListing) {
      throw new Error('Jun listing not found');
    }

    // Wait for favorite button and take pre-click screenshot
    await page.waitForSelector('[data-listing-id="jun_1"] .listing-favorite');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_click.png'));

    // Get initial localStorage state
    const initialStorage = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('airbnb_favorites') || '[]');
    });

    // Click favorite button
    await page.click('[data-listing-id="jun_1"] .listing-favorite');

    // Wait for state changes (animation/class changes)
    await page.waitForTimeout(1000);

    // Take post-click screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_click.png'));

    // Get updated localStorage
    const updatedStorage = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('airbnb_favorites') || '[]');
    });

    // Check button state change
    const buttonState = await page.evaluate(() => {
      const btn = document.querySelector('[data-listing-id="jun_1"] .listing-favorite');
      return {
        className: btn.className,
        isFavorite: btn.classList.contains('favorited') || btn.classList.contains('active')
      };
    });

    return {
      success: true,
      action: "Clicked favorite button on Jun's first listing and verified state change",
      extractedData: {
        initialStorage,
        updatedStorage,
        buttonState,
        isFavoriteToggled: initialStorage.includes('jun_1') !== updatedStorage.includes('jun_1')
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

