import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for listings to load
    await page.waitForSelector('[data-listing-id]', { timeout: 10000 });

    // Find Jun's first listing (jun_1)
    const junListing = await page.$('[data-listing-id="jun_1"]');
    if (!junListing) {
      throw new Error('Could not find Jun\'s first listing (jun_1)');
    }

    // Wait for favorite button to be visible
    const favoriteButton = await junListing.$('.listing-favorite');
    if (!favoriteButton) {
      throw new Error('Could not find favorite button in Jun\'s listing');
    }

    // Get initial localStorage state
    const initialFavorites = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('airbnb_favorites') || '[]');
    });

    // Get initial button state
    const initialButtonState = await favoriteButton.evaluate(btn => ({
      classList: btn.className,
      innerHTML: btn.innerHTML
    }));

    // Take screenshot before click
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_click.png'));

    // Click the favorite button
    await favoriteButton.click();

    // Wait for state changes (use Promise.all to wait for multiple conditions)
    await Promise.all([
      page.waitForTimeout(500), // Allow time for UI updates
      page.waitForFunction(() => {
        const btn = document.querySelector('[data-listing-id="jun_1"] .listing-favorite');
        return btn && btn.className.includes('favorited');
      }, { timeout: 5000 })
    ]);

    // Get updated button state
    const updatedButtonState = await favoriteButton.evaluate(btn => ({
      classList: btn.className,
      innerHTML: btn.innerHTML
    }));

    // Get updated localStorage
    const updatedFavorites = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('airbnb_favorites') || '[]');
    });

    // Take screenshot after click
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_click.png'));

    return {
      success: true,
      action: "Clicked favorite button on Jun's first listing and verified state changes",
      extractedData: {
        initialFavorites,
        updatedFavorites,
        initialButtonState,
        updatedButtonState,
        favoritesChanged: JSON.stringify(initialFavorites) !== JSON.stringify(updatedFavorites)
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
