import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for listings to load
    await page.waitForSelector('[data-listing-id="jun_1"]', { timeout: 10000 });

    // Scroll to the jun_1 listing to ensure it's in view
    const listingElement = await page.$('[data-listing-id="jun_1"]');
    await listingElement.scrollIntoViewIfNeeded();

    // Get initial localStorage state
    const initialStorage = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('airbnb_favorites') || '[]');
    });

    // Take screenshot before clicking favorite
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_click.png'));

    // Get initial button state
    const initialButtonClass = await page.$eval(
      '[data-listing-id="jun_1"] .listing-favorite',
      el => el.className
    );

    // Click the favorite button
    await page.click('[data-listing-id="jun_1"] .listing-favorite');

    // Wait for state changes
    await page.waitForTimeout(1000);

    // Get updated button state
    const updatedButtonClass = await page.$eval(
      '[data-listing-id="jun_1"] .listing-favorite',
      el => el.className
    );

    // Get updated localStorage
    const updatedStorage = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('airbnb_favorites') || '[]');
    });

    // Take screenshot after clicking favorite
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_click.png'));

    return {
      success: true,
      action: "Clicked favorite button for Jun's first listing and verified state changes",
      extractedData: {
        initialButtonState: initialButtonClass,
        updatedButtonState: updatedButtonClass,
        initialFavorites: initialStorage,
        updatedFavorites: updatedStorage,
        jun1InFavorites: updatedStorage.includes('jun_1')
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

