import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for listings to load
    await page.waitForSelector('[data-listing-id]', { timeout: 5000 });

    // Find Jun's first listing using data attribute selector
    const junListing = await page.$('[data-listing-id="jun_1"]');
    if (!junListing) {
      throw new Error('Jun listing not found');
    }

    // Take screenshot of the listing
    await junListing.screenshot({ path: path.join(screenshotsDir, 'step_2_jun_listing.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_jun_listing.png'));

    // Find the favorite button within the listing
    const favoriteBtn = await junListing.$('.listing-favorite');
    if (!favoriteBtn) {
      throw new Error('Favorite button not found');
    }

    // Get initial localStorage state
    const initialStorage = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('airbnb_favorites') || '[]');
    });

    // Click the favorite button
    await favoriteBtn.click();
    await page.waitForTimeout(1000); // Wait for state update

    // Take screenshot after click
    await junListing.screenshot({ path: path.join(screenshotsDir, 'step_3_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_click.png'));

    // Check updated localStorage
    const updatedStorage = await page.evaluate(() => {
      return JSON.parse(localStorage.getItem('airbnb_favorites') || '[]');
    });

    // Check button state change
    const isFavorited = await favoriteBtn.evaluate(btn => 
      btn.classList.contains('active') || btn.getAttribute('data-favorited') === 'true'
    );

    return {
      success: true,
      action: "Clicked favorite button on Jun's first listing and verified state change",
      extractedData: {
        initialFavorites: initialStorage,
        updatedFavorites: updatedStorage,
        isNowFavorited: isFavorited
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
