import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the 'jun_2' accommodation card to open the modal
    await page.waitForSelector("[data-listing-id='jun_2']");
    await page.click("[data-listing-id='jun_2']");

    // Wait for the modal to appear
    await page.waitForSelector('.modal-overlay');

    // Take screenshot of the modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_opened.png'));

    // Verify the modal title
    const modalTitle = await page.textContent('#modal-title');
    const expectedTitle = 'Expected Title for Jun_2'; // Replace with the actual expected title
    if (modalTitle.trim() === expectedTitle) {
      return {
        success: true,
        action: "Modal opened and title verified successfully",
        extractedData: { modalTitle },
        screenshots,
        error: null
      };
    } else {
      throw new Error(`Modal title mismatch: expected "${expectedTitle}", got "${modalTitle}"`);
    }

  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

