import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the specific listing card to be visible and click it
    await page.waitForSelector("[data-listing-id='jun_2']", { timeout: 5000 });
    await page.click("[data-listing-id='jun_2']");

    // Wait for the modal overlay to show up
    await page.waitForSelector('.modal-overlay', { timeout: 5000 });

    // Take a screenshot of the modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_opened.png'));

    // Verify the modal title
    const modalTitle = await page.$eval('#modal-title', el => el.innerText.trim());
    const expectedTitle = "Expected Title Here"; // Replace with the actual expected title

    // Check if the modal title is correct
    const isTitleCorrect = modalTitle === expectedTitle;

    return {
      success: true,
      action: "Clicked on the listing card and verified the modal title",
      extractedData: {
        modalTitle,
        isTitleCorrect
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
