import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the 'jun_2' listing card
    await page.waitForSelector("[data-listing-id='jun_2']");
    await page.click("[data-listing-id='jun_2']");

    // Wait for the modal to appear
    await page.waitForSelector('.modal-overlay');

    // Take screenshot of the modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_opened.png'));

    // Verify the modal title
    await page.waitForSelector('#modal-title');
    const modalTitle = await page.textContent('#modal-title');

    // Check if the modal title is correct
    const expectedTitle = "Expected Title Here"; // Replace with the actual expected title
    if (modalTitle === expectedTitle) {
      console.log("Modal title is correct.");
    } else {
      console.log("Modal title is incorrect.");
    }

    return {
      success: true,
      action: "Clicked on jun_2 listing card and verified modal title",
      extractedData: { modalTitle },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

