  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click on jun_2 listing card
    await page.waitForSelector("[data-listing-id='jun_2']");
    await page.click("[data-listing-id='jun_2']");

    // Take screenshot after clicking listing
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_click.png'));

    // Wait for modal to appear and verify title
    await page.waitForSelector('.modal-overlay');
    await page.waitForSelector('#modal-title');

    const modalTitle = await page.textContent('#modal-title');

    // Take screenshot of modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_modal_open.png'));

    return {
      success: true,
      action: "Clicked jun_2 listing card and verified modal title display",
      extractedData: {
        modalTitle: modalTitle
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for listings to load and find jun_2 card using data attribute
    await page.waitForSelector('[data-listing-id="jun_2"]', { timeout: 10000 });

    // Scroll to the jun_2 card to ensure it's in view
    await page.evaluate(() => {
      const card = document.querySelector('[data-listing-id="jun_2"]');
      if (card) card.scrollIntoView({ behavior: 'smooth', block: 'center' });
    });

    // Click on the jun_2 listing card
    await page.click('[data-listing-id="jun_2"]');

    // Wait for modal to appear with multiple selector strategies
    await Promise.race([
      page.waitForSelector('.modal-overlay', { state: 'visible', timeout: 5000 }),
      page.waitForSelector('#modal-title', { state: 'visible', timeout: 5000 }),
      page.waitForSelector('[role="dialog"]', { state: 'visible', timeout: 5000 })
    ]);

    // Take screenshot after modal opens
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_open.png'));

    // Extract modal title text
    const modalTitle = await page.$eval('#modal-title', el => el.textContent.trim());

    // Verify modal title is not empty
    if (!modalTitle) {
      throw new Error('Modal title is empty');
    }

    return {
      success: true,
      action: "Successfully clicked jun_2 listing card and verified modal title",
      extractedData: {
        modalTitle: modalTitle
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    const errorScreenshot = path.join(screenshotsDir, 'error.png');
    await page.screenshot({ path: errorScreenshot });
    screenshots.push(errorScreenshot);

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

