import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the jun_2 listing card
    const listingCardSelector = "[data-listing-id='jun_2']";
    await page.waitForSelector(listingCardSelector, { timeout: 10000 });
    await page.click(listingCardSelector);

    // Wait for modal to appear and take screenshot
    await page.waitForSelector('.modal-overlay', { state: 'visible', timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_opened.png'));

    // Extract modal title text
    const modalTitle = await page.$eval('#modal-title', el => el.textContent.trim());

    // Verify modal title is displayed
    await page.waitForSelector('#modal-title:not(:empty)');
    const isTitleVisible = await page.isVisible('#modal-title');

    // Take final screenshot of modal with title
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_modal_title.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_modal_title.png'));

    return {
      success: true,
      action: "Clicked jun_2 listing card and verified modal title display",
      extractedData: {
        modalTitle: modalTitle,
        isTitleVisible: isTitleVisible
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take screenshot on error
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the jun_2 listing card to be visible
    const listingSelector = "[data-listing-id='jun_2']";
    await page.waitForSelector(listingSelector, { state: 'visible' });

    // Click the jun_2 listing card
    await page.click(listingSelector);

    // Wait for modal to appear with multiple selector strategies
    await Promise.race([
      page.waitForSelector('.modal-overlay', { state: 'visible' }),
      page.waitForSelector('#modal-title', { state: 'visible' }),
      page.waitForTimeout(2000) // Fallback timeout
    ]);

    // Take screenshot after modal opens
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_opened.png'));

    // Extract modal title text
    const modalTitle = await page.textContent('#modal-title');

    // Verify modal title is displayed correctly
    if (!modalTitle || modalTitle.trim() === '') {
      throw new Error('Modal title is empty or not visible');
    }

    // Take final screenshot with modal content
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_modal_content.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_modal_content.png'));

    return {
      success: true,
      action: "Successfully clicked jun_2 listing card and verified modal title",
      extractedData: {
        modalTitle: modalTitle.trim()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    const errorScreenshot = path.join(screenshotsDir, 'error.png');
    await page.screenshot({ path: errorScreenshot });

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [errorScreenshot],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for listings to load
    await page.waitForSelector('[data-listing-id]', { timeout: 10000 });

    // Find and click the jun_2 listing using attribute selector
    const jun2Listing = await page.$('[data-listing-id="jun_2"]');
    if (!jun2Listing) {
      throw new Error('jun_2 listing not found');
    }

    await jun2Listing.click();
    await page.waitForTimeout(1000); // Wait for modal animation

    // Wait for modal to appear with multiple selector strategies
    await page.waitForSelector('.modal-overlay', { state: 'visible', timeout: 5000 });
    await page.waitForSelector('#modal-title', { timeout: 5000 });

    // Extract modal title text
    const modalTitle = await page.$eval('#modal-title', el => el.textContent.trim());

    // Take screenshot of the modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_open.png'));

    return {
      success: true,
      action: "Clicked jun_2 listing and verified modal title",
      extractedData: {
        modalTitle: modalTitle
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
