    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for listings to load
    await page.waitForSelector('[data-listing-id]', { timeout: 10000 });

    // Find and click the jun_2 listing using attribute selector
    const jun2Listing = await page.$('[data-listing-id="jun_2"]');
    if (!jun2Listing) {
      throw new Error('jun_2 listing not found');
    }

    await jun2Listing.click();

    // Wait for modal to appear with multiple selectors
    await Promise.all([
      page.waitForSelector('.modal-overlay', { timeout: 5000 }),
      page.waitForSelector('#modal-title', { timeout: 5000 })
    ]);

    // Wait for modal animation to complete
    await page.waitForTimeout(1000);

    // Take screenshot of modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_open.png'));

    // Extract modal title text
    const modalTitle = await page.$eval('#modal-title', el => el.textContent.trim());

    // Verify modal title is not empty
    if (!modalTitle) {
      throw new Error('Modal title is empty');
    }

    return {
      success: true,
      action: "Clicked jun_2 listing and verified modal title",
      extractedData: {
        modalTitle: modalTitle
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    try {
      await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
      screenshots.push(path.join(screenshotsDir, 'error.png'));
    } catch (screenshotError) {
      console.error('Failed to take error screenshot:', screenshotError);
    }

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

