import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click the profile button to open the dropdown menu
    await page.waitForSelector('.profile-btn');
    await page.click('.profile-btn');

    // Wait for the dropdown to be visible
    await page.waitForSelector('#profileDropdown');

    // Verify the 'Profile' link's href attribute
    const profileLink = await page.$('a:contains("Profile")');
    const profileHref = await profileLink.evaluate(el => el.getAttribute('href'));

    // Take a screenshot after opening the dropdown
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_dropdown_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_dropdown_opened.png'));

    // Check if the href is correct
    const isHrefCorrect = profileHref === 'profile.html';

    return {
      success: true,
      action: "Profile button clicked and href verified",
      extractedData: { profileHref, isHrefCorrect },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

