import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on the profile button to open the dropdown
    await page.waitForSelector('.profile-btn');
    await page.click('.profile-btn');

    // Wait for the dropdown to be visible
    await page.waitForSelector('#profileDropdown', { state: 'visible' });

    // Verify the 'Profile' link and its href attribute
    const profileLinkSelector = '#profileDropdown a[href="profile.html"]';
    await page.waitForSelector(profileLinkSelector);

    // Extract the href attribute
    const profileLinkHref = await page.getAttribute(profileLinkSelector, 'href');

    // Take screenshot of the dropdown
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_profile_dropdown.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_profile_dropdown.png'));

    // Validate the href attribute
    const isHrefCorrect = profileLinkHref === 'profile.html';

    return {
      success: true,
      action: "Verified 'Profile' link href attribute",
      extractedData: { href: profileLinkHref, isCorrect: isHrefCorrect },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
