import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click the profile button to open the dropdown menu
    await page.waitForSelector('button.profile-btn');
    await page.click('button.profile-btn');

    // Wait for the profile dropdown to be visible
    await page.waitForSelector('#profileDropdown', { state: 'visible' });

    // Verify if the 'Profile' link exists and check its href attribute
    const profileLinkSelector = 'a:contains("Profile")';
    await page.waitForSelector(profileLinkSelector);
    const profileLinkHref = await page.getAttribute(profileLinkSelector, 'href');

    // Take screenshot of the profile dropdown
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_dropdown_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_dropdown_opened.png'));

    // Validate the href attribute
    const expectedHref = 'profile.html';
    const isHrefCorrect = profileLinkHref === expectedHref;

    return {
      success: isHrefCorrect,
      action: isHrefCorrect ? "Successfully verified the 'Profile' link href." : "Failed to verify the 'Profile' link href.",
      extractedData: { profileLinkHref },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
