import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click profile button
    await page.waitForSelector('.profile-btn');
    await page.click('.profile-btn');

    // Wait for dropdown menu to appear
    await page.waitForSelector('#profileDropdown', { state: 'visible' });

    // Take screenshot after opening dropdown
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_dropdown_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_dropdown_open.png'));

    // Extract Profile link href attribute
    const profileHref = await page.$eval("a[href='profile.html']", el => el.getAttribute('href'));
    const linkText = await page.$eval("a[href='profile.html']", el => el.textContent.trim());

    return {
      success: true,
      action: "Clicked profile button to open dropdown and verified Profile link href",
      extractedData: {
        profileLink: {
          href: profileHref,
          text: linkText,
          isValid: profileHref === 'profile.html'
        }
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for profile button and click it
    await page.waitForSelector('.profile-btn', { timeout: 5000 });
    await page.click('.profile-btn');

    // Wait for dropdown menu to appear
    await page.waitForSelector('#profileDropdown', { state: 'visible', timeout: 5000 });

    // Take screenshot after opening dropdown
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_dropdown_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_dropdown_open.png'));

    // Find and verify the Profile link
    const profileLink = await page.$('a:has-text("Profile")');
    const hrefValue = await profileLink.getAttribute('href');

    // Verify href attribute
    const isHrefCorrect = hrefValue === 'profile.html';

    return {
      success: isHrefCorrect,
      action: "Clicked profile button to open dropdown and verified Profile link href",
      extractedData: {
        profileHref: hrefValue,
        isCorrect: isHrefCorrect
      },
      screenshots,
      error: isHrefCorrect ? null : `Expected href to be 'profile.html' but got '${hrefValue}'`
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

