import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click profile button
    await page.waitForSelector('.profile-btn', { state: 'visible' });
    await page.click('.profile-btn');

    // Wait for dropdown menu to appear
    await page.waitForSelector('#profileDropdown', { state: 'visible' });

    // Take screenshot after opening dropdown
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_dropdown_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_dropdown_open.png'));

    // Extract profile link data
    const profileLink = await page.$eval('a[href="profile.html"]', el => ({
      href: el.href,
      text: el.textContent.trim(),
      visible: el.offsetParent !== null
    }));

    return {
      success: true,
      action: "Clicked profile button and verified Profile link href attribute",
      extractedData: {
        profileLink: profileLink
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

